// service统一出口
import caiRequest from "./request/caiRequest"
import { ElLoading } from "element-plus";

const axios_config = {
    headers: {
        'X-Bmob-Application-Id': 'e536863a0210f9b43a1e5344b785de3d',
        'X-Bmob-REST-API-Key': '37b324a62cedd302e6a22fc84e622a5a',
        'Content-Type': 'application/json'
    },
    baseURL: 'https://api2.bmob.cn',
    timeout: 5000,
    "Access-Control-Allow-Origin": "*"
};
const cRequest1 = new caiRequest({
    axios_config,
    Interceptors: {
        a: null,
        requestInterceptor: (config) => {
            console.log('请求成功的拦截');
            this.cRequest1.a = ElLoading.service({ fullscreen: true });
            return config;
        },
        requestInterceptorCatch: (config) => {
            console.log('请求后的拦截');
            this.cRequest1.Interceptors.a.close();
            return Promise.reject(error);
        }
    }
});

export default cRequest1;
