function pathMapBreadcrumbs(userMenus, currentPath) {
    let breadcrumbs = [];
    // console.log(userMenus);

    console.log("面包写", breadcrumbs);
    return breadcrumbs;
};
let breadcrumbs = [];

function getdata(userMenus, currentPath) {
    for (let menu of userMenus) {
        if (menu.type === 1) {
            let menuname = menu.name,
                menuurl = menu.url;
            if (menu.children == null || menu.children.length < 1) {
                menu.children = [];
            }
            const findMenu = pathMapBreadcrumbs(menu.children, currentPath)
            if (findMenu) {
                breadcrumbs.push({ name: menuname, path: menu.url })
                breadcrumbs.push({ name: findMenu.name, path: findMenu.url });
                return findMenu;
                // return breadcrumbs;
            }
        } else if ((menu.type === 2) && menu.url === currentPath) {
            return menu;
        }
    }
}
export default pathMapBreadcrumbs
