export default {
  name: "myTable",
  directives: {
    // 自定义指令,用于可编辑input自动获取焦点
    focus: {
      inserted: function (e) {
        e.querySelector('input').focus();
      }
    }
  },
  props: {
    tableData: {
      type: Array,
      default: () => {}
    },
    columns: {
      type: Array,
      default: () => {}
    },
    pageParams: {
      type: Object,
      default: function () {
        return {
          page: 1,
          limit: 20,
          total: 0
        };
      }
    }
  },
  methods: {
    // 条数变化
    handleSizeChange(e) {
      this.$emit('handleSizeChange', e);
    },

    // 页码变化
    handleCurrentChange(e) {
      this.$emit('handleCurrentChange', e);
    }

  }
};
