// service统一出口
import caiRequest from "./request/caiRequest"
import { ElLoading, ElMessage } from "element-plus";

const axios_config = {
    headers: {
        'X-Bmob-Application-Id': 'e536863a0210f9b43a1e5344b785de3d',
        'X-Bmob-REST-API-Key': '37b324a62cedd302e6a22fc84e622a5a',
        'Content-Type': 'application/json'
    },
    baseURL: 'https://api2.bmob.cn',
    timeout: 5000,
    "Access-Control-Allow-Origin": "*"
};
let loadingInstance = null,
    isShowLoading = true;

const cRequest1 = new caiRequest({
    axios_config,
    isShowLoading,
    Interceptors: {
        requestInterceptor: (config) => {
            console.log('请求成功的拦截');
            if (isShowLoading) {
                loadingInstance = ElLoading.service({ fullscreen: true, text: "正在请求数据..." });
            }
            return config;
        },
        requestInterceptorCatch: (config) => {
            console.log('请求错误的拦截');
            return Promise.reject(error);
        },
        responseInterceptor: (response) => {
            console.log('响应成功的拦截');
            console.log(response);
            loadingInstance.close();
            return response;
        },
        responseInterceptorCatch: (error) => {
            console.log('响应后的拦截');
            loadingInstance.close();
            //状态码判断
            statusShow(error.response.status);
            return error;
        }
    }
});

function statusShow(statusCode) {
    switch (statusCode) {
        case 400:
            ElMessage({
                message: error.message,
                type: 'error',
                duration: 5 * 1000
            });
            break;
    }
}
export default cRequest1;
