function pathMapToMenu(userMenus, currentPath) {
    for (let menu of userMenus) {
        if (menu.type === 1) {
            console.log("孩子", menu.children);
            const findMenu = pathMapToMenu(menu.children ? null : [], currentPath)
            if (findMenu) {
                return findMenu;
            }
        } else if (menu.type === 3 && menu.url === currentPath) {
            return menu;
        }
    }
};
export default pathMapToMenu
