import "core-js/modules/es.error.cause.js";
export default {
  data() {
    let checkLen = (rule, value, callback) => {
      if (value.length < 6 || value.length > 14) {
        return callback(new Error('长度须在6-14之间'));
      }
    };

    let checkConfirmPwd = (rule, value, callback) => {
      if (value != this.account.password) {
        return callback(new Error('两次密码输入不一致'));
      }
    };

    return {
      account: {
        username: "",
        password: "",
        confirm_pwd: "",
        isRemberPwd: true
      },
      rules: {
        // 需要和v-model里面的字段名一致
        username: [{
          pattern: /^[\x7f-\xffA-Za-z0-9_]+$/,
          message: "不允许中文",
          trigger: "blur"
        }, {
          required: true,
          message: '用户名不能为空',
          trigger: 'blur'
        }, {
          validator: checkLen,
          trigger: 'blur'
        }],
        password: [{
          required: true,
          message: '密码不能为空',
          trigger: 'blur'
        }, {
          validator: checkLen,
          trigger: 'blur'
        }],
        confirm_pwd: [{
          required: true,
          message: '请再次输入密码',
          trigger: 'blur'
        }, {
          validator: checkConfirmPwd,
          trigger: 'blur'
        }]
      }
    };
  }

};
