import { createRouter } from "vue-router";

function mapMenusToRouter(userMenus) {
    const routers = [];
    // 默认加载所有的routes
    const allRoutes = [];
    //require.context()属于webpack的工具 可以加载整个文件夹
    const routerFiles = require.context("../router/main", true, /\.js/);
    routerFiles.keys().forEach((key) => {
        const route = require("../router/main" + key.split('.')[1])
        allRoutes.push(route.default);
    });
    console.log("所有main下面的路由", allRoutes);
    //根据菜单获取需要加载的routes
    //type==3->url->route
    //type==1->children-->type===2
    const _recurseGetRoute = (menus) => {
        console.log(menus);
        // for (let menu of menus) {
        for (let i = 0; i < menus.length; i++) {
            if (menus[i].type === 3) {
                const route = allRoutes.find((route) => {
                    route.url = menus[i].url;
                });
                if (route) {
                    routers.push(route);
                }
            } else {
                _recurseGetRoute(menus[i].children);
            }
        }
        // }
    }
    _recurseGetRoute(userMenus);
    return routers;
}
export default mapMenusToRouter
