// import VueRouter from "vue-router";
import { createRouter, createWebHistory } from "vue-router"
import { RouteRecordRaw } from 'vue-router'

import LoginPlane from "../views/login/cpns/login-panel";
import LoginName from "../views/login/login-name";
import HelloWorld from "../components/HelloWorld";

let router = createRouter({
    history: createWebHistory(),
    routes: [ // 路由列表，里面的每一个成员都是一个url地址和组件的映射关系
        {
            path: "/",
            component: LoginName,
            // redirect: "/login"
        },
        {
            path: "/hi",
            component: HelloWorld,
            meta: {
                title: '这里是title'
            }
        }
    ],
    children: [{
        path: "wu",
        name: "wu",
        component: LoginPlane
    }]
});

//设置登录过期时间（一天）
let EXPIRESTIME = 86400000;

// 校验登录
router.beforeEach(function(to, from, next) {
    //页面是否登录，本地存储中是否有token(uid)数据，否：跳转登录页面
    let item = localStorage.getItem("token");
    try {
        item = JSON.parse(item);
    } catch (error) {
        // eslint-disable-next-line no-self-assign
        item = item;
    }
    if (item) {
        let date = new Date().getTime();
        // 如果大于就是过期了，如果小于或等于就还没过期
        if (date - item.startTime > EXPIRESTIME) {
            localStorage.removeItem('token');
            next({
                name: "login"
            });
        } else {
            next();
        }
    } else {
        next({
            name: "login"
        });
    }
});
export default router
