import axios from 'axios'

// let axiosInterceptors = {
//     requestInterceptor: null,
//     requestInterceptorCatch: null,
//     responseInterceptor: null,
//     responseInterceptorCatch: null
// }

class caiRequest {
    constructor(config) {
        this.instance = axios.create(config.axios_config);
        // this.Interceptors = axiosInterceptors;
        this.Interceptors = config.Interceptors;
        //axios拦截请求前后
        this.instance.interceptors.request.use(
            this.Interceptors.requestInterceptor,
            this.Interceptors.requestInterceptorCatch
        );
        //axios拦截响应前后
        this.instance.interceptors.response.use(
            this.Interceptors.responseInterceptor,
            this.Interceptors.responseInterceptorCatch
        );
    }
    request() {

        this.instance.get("/1/classes/_User", this.config.axios_config).then((res) => {
            console.log(res);
        })
    }
}
export default caiRequest;
